let i,acc=document.querySelectorAll(".accordion_bnt");for(i=0;i<acc.length;i++)acc[i].addEventListener("click",function(){this.classList.toggle("active");let t=this.nextElementSibling;t.style.maxHeight?t.style.maxHeight=null:t.style.maxHeight=t.scrollHeight+"px"});let testContainer=Array.from(document.querySelectorAll(".test-container .client")),current=0;function changeTest(){current<0?current=testContainer.length-1:current>testContainer.length-1&&(current=0),testContainer.forEach(t=>{t.style.display="none",t.classList.add("rotate")}),testContainer[current].style.display="flex"}let prevButtonTest=document.querySelector(".prev_test");prevButtonTest.addEventListener("click",()=>{current--,changeTest()});let nextButtonTest=document.querySelector(".next_test");nextButtonTest.addEventListener("click",()=>{current++,changeTest()});