let id=e=>document.getElementById(e),classes=e=>document.getElementsByClassName(e),username=id("username"),password=id("password"),email=id("email"),form=id("form"),submit=classes("submit"),errorMsg=classes("error_message"),errorMsgPassword=classes("error_password");form.addEventListener("submit",e=>{e.preventDefault(),engine(password,2,"Password cannot be blank"),engine(email,1,"Email cannot be blank"),engine(username,0,"Username cannot be blank")});let engine=(e,s,r)=>{""===e.value.trim()?(errorMsg[s].innerHTML=r,e.style.border="2px solid red"):(errorMsg[s].innerHTML="",e.style.border="2px solid green",location.href="index.html")},showPassword=document.querySelector("#show_password");showPassword.addEventListener("click",()=>{"password"===password.type?(password.type="text",showPassword.className="fa fa-eye-slash"):(password.type="password",showPassword.className="fa fa-eye")});