let id=e=>document.getElementById(e),classes=e=>document.getElementsByClassName(e),password=id("password"),email=id("email"),form=id("form"),errorMsg=classes("error_message");form.addEventListener("submit",e=>{e.preventDefault(),engine(password,1,"Password cannot be blank"),engine(email,0,"Email cannot be blank")});let engine=(e,s,r)=>{""===e.value.trim()?(errorMsg[s].innerHTML=r,e.style.border="2px solid red"):(errorMsg[s].innerHTML="",e.style.border="2px solid green",location.href="index.html")},showPassword=document.querySelector("#show_password");showPassword.addEventListener("click",()=>{"password"===password.type?(password.type="text",showPassword.className="fa fa-eye-slash"):(password.type="password",showPassword.className="fa fa-eye")});